import React, { Component } from 'react';
import SearchList from './SearchList';
const axios = require('axios');

class SearchBar extends Component {

    state = {
        results:[],
        searchInput: '',
        isFetching: false
    }

    searchInputChange = e => {
        this.setState({
            searchInput: e.target.value,
            isFetching: true
        })
        if (e.target.value.length > 3 && e.target.value.trim() !== '') {
            const API_KEY = 'e7a42390';
            const URL = `http://www.omdbapi.com/?apikey=${API_KEY}&s=${e.target.value}`;
            axios.get(URL)
                .then(res => {
                    if (res.data.Response === "True") {
                        console.log(res.data);
                        this.setState({
                            results: res.data.Search,
                            isFetching: false
                        })
                    }

                })
            .catch(err => console.log(err))
        }
    }
    render() {
        const {results, searchInput, isFetching } = this.state;
        return (
            <div className="search">
                <input type="text" placeholder="Search... " value={searchInput} onChange={this.searchInputChange}/>
                {
                    results.length === 0 && searchInput.trim() === ''
                    &&
                    <p>Please enter title name</p>
                }
                {
                    results.length === 0 && searchInput.trim() !== ''
                    &&
                    <p>No results</p>
                }
                <SearchList results={results}/>
            </div>

        )
    }
}


export default SearchBar;
