import React from 'react';

const PosterStyle = {
    width: '45px',
    height: '45px'
};

const ItemList = (props) => {
    const { Title, Year, Type, Poster } = props.result;
    return (
        <ul>
            <li>Title: {Title}</li>
            <li>Type: {Type}</li>
            <li>Year: {Year}</li>
            <img src={Poster} alt={Title} style={PosterStyle}/>
        </ul>
    )
}

const SearchList = (props) => {
    if (props.results.length !== 0) {
        return (
            <div>
                {props.results.map(result => (
                    <ItemList result={result} key={result.imdbID}/>
                ))}
            </div>
        )
    } else {
        return (
            <p> No results.. </p>
        )
    }

}

export default SearchList;
