import React, { Component } from 'react';
const axios = require('axios');

class SearchBar extends Component {

    state = {
        searchInput:''
    }
    // componentDidMount() {
    //     // const API_KEY = 'e7a42390';
    //     // const URL = 'http://www.omdbapi.com/?apikey=' + API_KEY +'&s=blade runner';
    //     // axios.get(URL)
    //     //     .then(res => {
    //     //         let results = res.data;
    //     //         console.log(results);
    //     //     })
    //     //     .catch(err => console.log(err))
    // }
    searchInputChange = e => {
        if (e.target.value > 3) {
            this.setState({
                searchInput: e.target.value
            });
            const API_KEY = 'e7a42390';
            const URL = 'http://www.omdbapi.com/?apikey=' + API_KEY +'&s=' + this.state.searchInput;
            axios.get(URL)
                .then(res => {
                    let results = res.data;
                    console.log(res);
                    console.log(results);
                })
            .catch(err => console.log(err))
        } else {
            console.log("Nothing happened!!!");
        }
    }
    render() {
        return (
            <input type="text" placeholder="Search..." onChange={this.searchInputChange}/>
        )
    }
}


export default SearchBar;
